import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';

const CameraFeed = () => {
  const [detected, setDetected] = useState(false);
  const [isCompleted, setIsCompleted] = useState(false);  // Track completion
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [processing, setProcessing] = useState(false);

  useEffect(() => {
    const video = videoRef.current;

    // Request access to the webcam
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        video.srcObject = stream;

        // Only call play() when the video is loaded
        video.onloadedmetadata = () => {
          video.play().catch(error => console.error("Error playing video:", error));
        };
      })
      .catch(err => console.error("Error accessing webcam:", err));

    return () => {
      if (video.srcObject) {
        video.srcObject.getTracks().forEach(track => track.stop()); // Stop webcam access when the component unmounts
      }
    };
  }, []);

  let lastProcessedTime = Date.now();

  const processFrame = async () => {
    const currentTime = Date.now();
    const timeDiff = currentTime - lastProcessedTime;

    // Adjust frame rate by only sending frames every 200ms (5 FPS, adjust as necessary)
    if (timeDiff < 200) {
      return;
    }

    lastProcessedTime = currentTime;

    const canvas = canvasRef.current;
    const bufferCanvas = document.createElement('canvas');  // Create an off-screen buffer canvas
    bufferCanvas.width = canvas.width;
    bufferCanvas.height = canvas.height;

    const context = canvas.getContext('2d');
    const bufferContext = bufferCanvas.getContext('2d');  // Context for the off-screen canvas

    // Draw the current frame from video on buffer canvas
    bufferContext.drawImage(videoRef.current, 0, 0, bufferCanvas.width, bufferCanvas.height);

    // Convert the frame to Blob and send to the backend for processing
    bufferCanvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append('image', blob, 'frame.jpg');

      try {
        setProcessing(true);
        const response = await axios.post('http://localhost:5000/detect', formData, {
          responseType: 'json'
        });

        // Handle the response
        const { detected, augmented_image } = response.data;

        // Create an image object from the base64 encoded image
        const img = new Image();
        img.src = `data:image/jpeg;base64,${augmented_image}`;
        img.onload = () => {
          const canvasContext = canvas.getContext('2d');
          canvasContext.drawImage(img, 0, 0, canvas.width, canvas.height);  // Draw the augmented frame
          setProcessing(false);

          if (detected) {
            setIsCompleted(true);  // Mark detection as completed if id=0 is detected
          }

          setDetected(detected);  // Update detection state
        };

      } catch (err) {
        console.error("Error processing frame:", err);
        setProcessing(false);
        setDetected(false);  // Ensure detection state is false if an error occurs
        setIsCompleted(false);  // Reset completion on error
      }
    }, 'image/jpeg');
  };

  useEffect(() => {
    const interval = setInterval(processFrame, 1000 / 10); // Process 10 frames per second
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <video ref={videoRef} style={{ display: 'none' }} />
      <canvas ref={canvasRef} width="640" height="480" />
      {/* Display the text "Chemical pouring" only when marker is detected */}
      {detected && <p style={{ color: 'green', marginTop: '10px' }}>Chemical pouring</p>}
      {/* Display the text "Completed" when ArUco marker with id=0 is detected */}
      {isCompleted && <p style={{ color: 'blue', marginTop: '10px' }}>Completed</p>}
    </div>
  );
};

export default CameraFeed;
