import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';

const CameraFeed = () => {
  const [detected, setDetected] = useState(false);
  const [completed, setCompleted] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);  // Track the current step
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const audioRefs = useRef([
    new Audio('/voice1.mp3'), // Step 1: Introduction to sulfate test
    new Audio('/voice2.mp3'), // Step 2: Show distilled water
    new Audio('/voice3.mp3'), // Step 3: Show test tube 1
    // Add the rest of the steps here
    new Audio('/voice13.mp3'), // Step 13: Test completion message
  ]);

  useEffect(() => {
    // Play the first step's audio when the component is loaded
    audioRefs.current[0].play().catch(err => console.error("Audio play error:", err));

    // Web camera initialization logic
    const video = videoRef.current;
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        video.srcObject = stream;
        video.onloadedmetadata = () => {
          video.play().catch(error => console.error("Error playing video:", error));
        };
      })
      .catch(err => console.error("Error accessing webcam:", err));

    return () => {
      if (video.srcObject) {
        video.srcObject.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  const processFrame = async () => {
    if (completed) return;  // Stop processing if test is complete

    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    // Send frame to backend for ArUco detection
    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append('image', blob, 'frame.jpg');
      try {
        const response = await axios.post('http://localhost:5000/detect', formData, { responseType: 'json' });
        const { detected, ids } = response.data;

        if (detected && ids) {
          handleMarkerDetection(ids);
        }

        // Display processed frame (augmented) coming from backend
        const img = new Image();
        img.src = `data:image/jpeg;base64,${response.data.augmented_image}`;
        img.onload = () => context.drawImage(img, 0, 0, canvas.width, canvas.height);
      } catch (err) {
        console.error("Error processing frame:", err);
      }
    }, 'image/jpeg');
  };

  const handleMarkerDetection = (ids) => {
    const expectedMarkerId = currentStep;  // Assuming marker ID matches the current step
    if (ids.includes(expectedMarkerId)) {
      console.log(`Step ${currentStep} verified with marker ID: ${expectedMarkerId}`);

      // Move to the next step
      setCurrentStep(currentStep + 1);

      // Stop current audio and play the next
      if (currentStep < audioRefs.current.length) {
        audioRefs.current[currentStep].pause();
        audioRefs.current[currentStep + 1].play().catch(err => console.error("Audio play error:", err));
      }

      // Mark test as completed when last step is done
      if (currentStep === audioRefs.current.length - 1) {
        setCompleted(true);
      }
    }
  };

  useEffect(() => {
    const interval = setInterval(processFrame, 1000 / 10);  // 10 FPS
    return () => clearInterval(interval);
  }, [currentStep]);

  return (
    <div>
      <video ref={videoRef} style={{ display: 'none' }} />
      <canvas ref={canvasRef} width="640" height="480" />
      {completed && <p style={{ color: 'blue', marginTop: '10px' }}>Sulfate Ion Test Completed</p>}
    </div>
  );
};

export default CameraFeed;
