import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';

const CameraFeed = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [processing, setProcessing] = useState(false);

  useEffect(() => {
    const video = videoRef.current;

    // Request access to the webcam
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        video.srcObject = stream;

        // Only call play() when the video is loaded
        video.onloadedmetadata = () => {
          video.play().catch(error => console.error("Error playing video:", error));
        };
      })
      .catch(err => console.error("Error accessing webcam:", err));

    return () => {
      if (video.srcObject) {
        video.srcObject.getTracks().forEach(track => track.stop()); // Stop webcam access when the component unmounts
      }
    };
  }, []);


  let lastProcessedTime = Date.now();

  const processFrame = async () => {
    const currentTime = Date.now();
    const timeDiff = currentTime - lastProcessedTime;

    // Adjust frame rate by only sending frames every 200ms (5 FPS, adjust as necessary)
    if (timeDiff < 200) {
      return;
    }

    lastProcessedTime = currentTime;

    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    // Draw the current frame from video
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    // Convert the frame to Blob and send to the backend for processing
    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append('image', blob, 'frame.jpg');

      try {
        setProcessing(true);
        const response = await axios.post('http://localhost:5000/detect', formData, {
          responseType: 'blob'
        });

        // Create an image object from the returned blob
        const img = new Image();
        img.src = URL.createObjectURL(response.data);
        img.onload = () => {
          context.clearRect(0, 0, canvas.width, canvas.height);  // Clear the previous frame
          context.drawImage(img, 0, 0, canvas.width, canvas.height);  // Draw the new frame
          setProcessing(false);
        };
      } catch (err) {
        console.error("Error processing frame:", err);
        setProcessing(false);
      }
    }, 'image/jpeg');
  };


  useEffect(() => {
    const interval = setInterval(processFrame, 1000 / 10); // Process 10 frames per second
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <video ref={videoRef} style={{ display: 'none' }} />
      <canvas ref={canvasRef} width="640" height="480" />
      {processing && <p>Processing...</p>}
    </div>
  );
};

export default CameraFeed;
